mapper.select:
select date_format(tra.creater_time,'%H') dimension,count(tra.creater_time) resourceNum from t_resource_allot tra 

	
		and tra.province like CONCAT('%',#{rr.province},'%')
	
	
		and tra.city like CONCAT('%',#{rr.city},'%')
	
	
		and tra.channel = #{rr.channel}
	
	
		and TO_DAYS(tra.creater_time) >= TO_DAYS(#{rr.startDate})
	
	
		 and TO_DAYS(tra.creater_time) <= TO_DAYS(#{rr.endDate}) 
	
	
		and date_format(tra.creater_time,'%H') = #{rr.timeslot}
	
	
		and tra.executer = #{rr.executer}
	

GROUP BY date_format(tra.creater_time,'%H')

select date_format(tra.creater_time,'%H') dimension,count(tra.is_talk) talkNum from t_resource_allot tra

	tra.is_talk = 1
	
		and tra.province = #{rr.province}
	
	
		and tra.city = #{rr.city}
	
	
		and tra.channel = #{rr.channel}
	
	
		and TO_DAYS(tra.creater_time) >= TO_DAYS(#{rr.startDate})
	
	
		 and TO_DAYS(tra.creater_time) <= TO_DAYS(#{rr.endDate}) 
	
	
		and date_format(tra.creater_time,'%H') = #{rr.timeslot}
	
	
		and tra.executer = #{rr.executer}
	

GROUP BY date_format(tra.creater_time,'%H')

select date_format(ccc.enter_time,'%H') dimension,sum(ccc.apply_num + ccc.upgrade_num) orderNum from crm_customer_count ccc

 	
		and ccc.province = #{rr.province}
	
	
		and ccc.city = #{rr.city}
	
	
		and ccc.channel = #{rr.channel}
	
	
		and ccc.sex = #{rr.sex}
	
	
		and ccc.age = #{rr.age}
	
	
		and TO_DAYS(ccc.enter_time) >= TO_DAYS(#{rr.startDate})
	
	
		 and TO_DAYS(ccc.enter_time) <= TO_DAYS(#{rr.endDate}) 
	
	
		and date_format(ccc.enter_time,'%H') = #{rr.timeslot}
	
	
		and ccc.executer = #{rr.executer}
	

GROUP BY date_format(ccc.enter_time,'%H')

select date_format(ccc.enter_time,'%H') dimension,count(ccc.enter_time) customerNum from crm_customer_count ccc

	
		and ccc.province = #{rr.province}
	
	
		and ccc.city = #{rr.city}
	
	
		and ccc.channel = #{rr.channel}
	
	
		and ccc.sex = #{rr.sex}
	
	
		and ccc.age = #{rr.age}
	
	
		and TO_DAYS(ccc.enter_time) >= TO_DAYS(#{rr.startDate})
	
	
		 and TO_DAYS(ccc.enter_time) <= TO_DAYS(#{rr.endDate}) 
	
	
		and date_format(ccc.enter_time,'%H') = #{rr.timeslot}
	
	
		and ccc.executer = #{rr.executer}
	

GROUP BY date_format(ccc.enter_time,'%H')

select date_format(ccc.enter_time,'%H') dimension,sum(ccc.insure_money) insureMoney from crm_customer_count ccc

	
		and ccc.province = #{rr.province}
	
	
		and ccc.city = #{rr.city}
	
	
		and ccc.channel = #{rr.channel}
	
	
		and ccc.sex = #{rr.sex}
	
	
		and ccc.age = #{rr.age}
	
	
		and TO_DAYS(ccc.enter_time) >= TO_DAYS(#{rr.startDate})
	
	
		 and TO_DAYS(ccc.enter_time) <= TO_DAYS(#{rr.endDate}) 
	
	
		and date_format(ccc.enter_time,'%H') = #{rr.timeslot}
	
	
		and ccc.executer = #{rr.executer}
	

GROUP BY date_format(ccc.enter_time,'%H')
